// Siempre que creo un componente: export, declarar la funcion arrow con un retorno.
// La propiedad greeting la recibo como parametro desestructurado de App.js
// Le agrego un nombre de clase a la seccion que retornara este componente para poder darle un estilo.

// Importamos la hoja de estilo asociado a mi componente. Se recomienda tener una hoja de estilo para 
// cada componente.


import "./ItemListContainer.css"
import { Boton } from "../Boton/Boton"
import { Contenedor } from "../Contenedor/Contenedor"
import { Clicker } from "../Clicker/Clicker"
import {useState, useEffect} from 'react'
import { pedirDatos } from "../../helpers/pedirDatos"
import { Producto } from "../Producto/Producto"
import { stock } from "../../data/stock"
import { Item } from "../Item/Item"


export const ItemListContainer = ( {greeting} ) => {

    const [productos, setProductos] = useState ([])

    const [clicker, setClicker] = useState(true)

    const mostrar = ()=>{
        setClicker(!clicker)
    }


useEffect( () => {

    pedirDatos()
        .then( (res) => {
            setProductos(res)
        })
        .catch((err) => {
            console.log(err)
        })
        .finally(() => {
            console.log('Promesa finalizada')
        })


}, [])

    return (
        <Contenedor>
            <h2>{greeting}</h2>
            <hr />

            <div className="row">

            { stock.map( (el, i) => <Item  key={el.id} nombre={el.nombre} precio={el.precio} 
            img={el.img} desc={el.desc}/>)}

            </div>




            <Boton click={mostrar}>Mostrar clicker</Boton>

            <hr/>
            { clicker === true? <Clicker/> : null}

        </Contenedor>






    )
}
