import { Boton } from "../Boton/Boton"
import { useState} from 'react'

export const Clicker = () => {

    const [clicks, setClicks] = useState(0)
    //sintaxis para crear un estado en un componente de react.
    //debemos de importarla la funcion useState
    //lo que yo paso como parametro a useState sera el valor de mi variable clicks
    const aumentar = () => {
        setClicks( clicks + 1 )
    }

    return (
        <>
            <Boton click={aumentar}>Click Me!</Boton>
            <p>Clicks: {clicks}</p>
        </>
    )
}
